import React from "react";
import api from "../utils/api.js";

function Main(props) {
    const [userName, setUserName] = React.useState('');
    const [userDescription, setUserDescription] = React.useState('');
    const [userAvatar, setUserAvatar] = React.useState('');

    React.useEffect(() => {
        api.getUserData()
        .then(userInfo=>{
            setUserName (userInfo.name);
            setUserDescription (userInfo.about);
            setUserAvatar (userInfo.avatar);
          })
          .catch(err => {
            console.log(err);
          });
    })


    return (
        <main>
            <section className="profile">
                <img className="profile__icon" src="`url(${userAvatar})`" alt="Фото путешественника" />
                <button className="profile__update-avatar" onClick={props.onEditAvatar}></button>
                <div className="profile__info">
                    <div className="profile__redaction">
                        <h1 className="profile__title">{userName}</h1>
                        <button className="profile__button" type="button" onClick={props.onEditProfile}></button>
                    </div>
                    <p className="profile__pharagraph">{userDescription}</p>
                </div>
                <button className="profile__add" type="button" onClick={props.onAddPlace}></button>
            </section>

            <section className="elements">
                <ul className="elements__images">
                    <template id="card">
                        <li className="element">
                            <img className="element__image" src="<%=require('./images/karajaevsk.jpg')%>"
                                alt="" />
                            <button className="element__delete"></button>
                            <div className="element__name">
                                <h2 className="element__text"></h2>
                                <button className="element__button" type="button"></button>
                                <span className="element__like-sum">0</span>
                            </div>
                        </li>`
                    </template>
                </ul>
            </section>
        </main>
    );
}

export default Main
