import logo from '../images/Vector.svg';
import '../pages/index.css';
import Header from "./Header.js";
import Main from "./Main.js";
import Footer from './Footer.js';
import PopupWithForm from './PopupWithForm.js';
import React from 'react';
import api from '../utils/api.js';
import ImagePopup from "./ImagePopup.js"
import { CurrentUserContext } from "../contexts/CurrentUserContext.js";
import EditProfilePopup from './EditProfilePopup.js';
import EditAvatarPopup from './EditAvatarPopup.js';
import AddPlacePopup from './AddPlacePopup.js';
import DeleteCardPopup from './DeleteCardPopup.js';

function App() {
  const [isEditProfilePopupOpen, handleEditProfileClick] = React.useState(false);
  const [isAddPlacePopupOpen, handleAddPlaceClick] = React.useState(false);
  const [isEditAvatarPopupOpen, handleEditAvatarClick] = React.useState(false);
  const [selectedCard, handleCardClick] = React.useState(null);
  const [currentUser, setCurrentUser] = React.useState({});
  const [cards, setCards] = React.useState([]);



  function handleUpdateUser(userData) {
    api.changeUserData(userData.name, userData.about)
      .then((userData) => {
        setCurrentUser(userData);
      })
      .catch((err) => {
        console.log(err);
      })
      .finally(() => closeAllPopups());
  }

  function handleUpdateAvatar({ avatar }) {
    api.setProfileAvatar(avatar)
      .then((userAvatar) => {
        setCurrentUser(userAvatar)
      })
      .catch((err) => {
        console.log(err);
      })
      .finally(() => closeAllPopups());
  }

  function handleAddPlaceSubmit(name, link) {
    console.log(name, link)
    api.addNewCard(name, link)
    .then((newCard) => {
      setCards([newCard, ...cards]);
    })
    .catch((err) => {
      console.log(err);
    })
    .finally(() => closeAllPopups());
  }

  const closeAllPopups = () => {
    handleEditAvatarClick(false);
    handleEditProfileClick(false);
    handleAddPlaceClick(false);
    handleCardClick(null);
  }

  React.useEffect(() => {
    const initialPromises = Promise.all([
      api.getUserData(),
      api.getInitalCards()
    ]);

    initialPromises
      .then(([profile, Cards]) => {
        console.log('1234', profile)
        setCards(Cards);
        setCurrentUser(profile)
      })
      .catch(err => {
        console.log(err)
      })

  }, [])

  const  handleCardDelete = () => {
    api.deleteCard(card._id)
      .then(() => {
        setCards((state) => state.filter((c) => c._id !== card._id))
      })
      .catch(err => {
        console.log(err)
      })
  }

  function handleCardLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id);
    api
      .changeLikeCardStatus(card._id, !isLiked)
      .then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
      })
      .catch((err) => console.log(err));
  }



  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="background">
        <div className="page">
          <Header />
          <Main
            onCardDelete={handleCardDelete}
            onEditAvatar={handleEditAvatarClick}
            onEditProfile={handleEditProfileClick}
            onAddPlace={handleAddPlaceClick}
            onCardClick={handleCardClick}
            onCardLike={handleCardLike}
            cards={cards}
          />
          <Footer />
          <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} onUpdateUser={handleUpdateUser} />
          <AddPlacePopup isOpen={isAddPlacePopupOpen} onClose={closeAllPopups} onAddPlace={handleAddPlaceSubmit}/>
          <PopupWithForm
            name="confirmation-delete"
            title="Вы уверены?">
            <fieldset className="popup__fieldset">
              <button className="popup__button">Да</button>
            </fieldset>
          </PopupWithForm>

          <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} onUpdateAvatar={handleUpdateAvatar} />

          <ImagePopup
            card={selectedCard}
            onClose={closeAllPopups}
          />

        </div>
      </div >
    </CurrentUserContext.Provider>


  );
}

export default App;
