import React from 'react';
import { Link } from "react-router-dom";

class Login extends React.Component {
    constructor(props) {
        super(props);

        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleSubmit(e){
        e.preventDefault();
      if (!this.state.username || !this.state.password){
        return;
      }
      duckAuth.authorize(this.state.username, this.state.password)
      .then((data) => {
        if (data.jwt){
          this.setState({username: '', password: ''} ,() => {
              this.props.handleLogin();
              this.props.history.push('/ducks');
          })
        }  
      })
      .catch(err => console.log(err)); // запускается, если пользователь не найден
    } 
    render() {
        return(
            <section className="Register">
            <form name="register" className="field__form">
            <p className="field__welcome">Вход</p>
            <input id="email" name="email"  placeholder="Email" className="field__input" required/>
            <input id="password" type="password" placeholder="Пароль" name="password" className="field__input" required />
            <button name="submit" className="field__submit" type="submit" aria-label="Зарегестрироваться">Войти</button>
            </form>
        </section>
        )
    }
}

export default Login
