export const BASE_URL = 'https://api.nomoreparties.co';

export const register = (passwordUser, emailUser) => {
  return fetch(`${BASE_URL}/signup`, {
    method: 'POST',
    headers: {
      authorization: "7d67df57-04a3-4c0e-b94c-731b56c35104",
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({ password: passwordUser, email: emailUser })
  })
  .then((res) => {
      if (res.ok){
        return res.json();
      } 
      return Promise.reject(`Ошибка: ${res.status}`)
    })
}

export const authorization = (passwordUser, emailUser) => {
  return fetch(`${BASE_URL}/signin`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({ passwordUser, emailUser })
  })
  .then((res) => {
    if (res.ok){
      return res.json();
    } 
    return Promise.reject(`Ошибка: ${res.status}`)
  })
}
